import XCTest

@testable import Sweet

final class TestCompliancesAPI: XCTestCase {
  func testFetchComplianceJobs() async throws {
    let complianceJobs = try await Sweet.test.complianceJobs(type: .tweets)

    print(complianceJobs.first!)
  }

  func testFetchComplianceJob() async throws {
    let jobID = "1579194619895480320"
    let complianceJob = try await Sweet.test.complianceJob(jobID: jobID)

    print(complianceJob)
  }

  func testCreateComplianceJob() async throws {
    let complianceJob = try await Sweet.test.createComplianceJob(type: .tweets, name: "testJob")

    print(complianceJob)
  }

  func testUploadComplianceData() async throws {
    let url = URL(
      string:
        "https://storage.googleapis.com/twttr-tweet-compliance/1579194619895480320/submission/1468034183536984067_1579194619895480320?X-Goog-Algorithm=GOOG4-RSA-SHA256&X-Goog-Credential=complianceapi-public-svc-acct%40twttr-compliance-public-prod.iam.gserviceaccount.com%2F20221009%2Fauto%2Fstorage%2Fgoog4_request&X-Goog-Date=20221009T193832Z&X-Goog-Expires=900&X-Goog-SignedHeaders=content-type%3Bhost&X-Goog-"
    )!

    let tweetIDs = [
      "1537705231160836096",
      "1529225999111835648",
      "1529100586440097792",
      "1546086942969802752",
      "1527957294268710912",
      "1521767287778254848",
    ]

    try await Sweet.uploadComplianceData(uploadURL: url, ids: tweetIDs)
  }

  func testDownloadComplianceData() async throws {
    let url = URL(
      string:
        "https://storage.googleapis.com/twttr-tweet-compliance/1579194619895480320/delivery/1468034183536984067_1579194619895480320?X-Goog-Algorithm=GOOG4-RSA-SHA256&X-Goog-Credential=complianceapi-public-svc-acct%40twttr-compliance-public-prod.iam.gserviceaccount.com%2F20221009%2Fauto%2Fstorage%2Fgoog4_request&X-Goog-Date=20221009T193832Z&X-Goog-Expires=604800&X-Goog-SignedHeaders=host&X-Goog-"
    )!

    let compliances = try await Sweet.downloadComplianceData(downloadURL: url)

    print(compliances)
  }

  func testStreamUsersCompliance() async throws {
    let stream = TestStream()
    stream.testStreamUsersCompliance()
    let timeoutSeconds: UInt64 = 60 * 3 * 1_000_000_000
    try await Task.sleep(nanoseconds: timeoutSeconds)
  }

  func testStreamTweetsCompliance() async throws {
    let stream = TestStream()
    stream.testStreamTweetsCompliance()
    let timeoutSeconds: UInt64 = 60 * 3 * 1_000_000_000
    try await Task.sleep(nanoseconds: timeoutSeconds)
  }
}

private class TestStream: NSObject, URLSessionDataDelegate {
  func urlSession(_ session: URLSession, dataTask: URLSessionDataTask, didReceive data: Data) {
    print(String(decoding: data, as: UTF8.self))
  }

  func testStreamUsersCompliance() {
    let request = Sweet.test.streamUsersComplianceRequest(
      partition: 1, startTime: Date().addingTimeInterval(-123_456_789), endTime: Date())
    let session = URLSession(configuration: .default, delegate: self, delegateQueue: nil)
    session.dataTask(with: request).resume()
  }

  func testStreamTweetsCompliance() {
    let request = Sweet.test.streamTweetsComplianceRequest(
      partition: 1, startTime: Date().addingTimeInterval(-123_456_789), endTime: Date())
    let session = URLSession(configuration: .default, delegate: self, delegateQueue: nil)
    session.dataTask(with: request).resume()
  }
}
